import random

class Character:
    def __init__(self, name, health, power, special_power):
        self.name = name
        self.health = health
        self.power = power
        self.special_power = special_power
        self.special_moves = []

    def attack(self, opponent):
        damage = random.randint(self.power // 2, self.power)  # Damage range increased
        opponent.health -= damage
        print(f"{self.name} attacks {opponent.name} for {damage} damage!")

    def special_attack(self, opponent):
        if self.special_moves:
            move = random.choice(self.special_moves)
            damage = move['damage']
            opponent.health -= damage
            print(f"{self.name} uses {move['name']} on {opponent.name} for {damage} damage!")
        else:
            print(f"{self.name} has no special moves!")

    def heal(self):
        heal_amount = random.randint(30, 60)  # Healing range increased
        self.health += heal_amount
        print(f"{self.name} heals for {heal_amount}. Health is now {self.health}.")

    def is_alive(self):
        return self.health > 0

    def status(self):
        return f"{self.name}: Health = {self.health}"

class Player(Character):
    def __init__(self, name, character_class):
        super().__init__(name, health=150, power=50, special_power=100)  # Overpowered stats
        self.character_class = character_class
        self.special_moves = self.initialize_special_moves()
        self.inventory = [HealthPotion(), AttackBoost()]  # Start with items
        self.experience = 0
        self.level = 1

    def initialize_special_moves(self):
        if self.character_class == "Warrior":
            return [{'name': 'Devastating Charge', 'damage': random.randint(80, 120)}]
        elif self.character_class == "Mage":
            return [{'name': 'Inferno Blast', 'damage': random.randint(90, 130)}]
        elif self.character_class == "Rogue":
            return [{'name': 'Shadow Strike', 'damage': random.randint(85, 125)}]
        return []

    def gain_experience(self, amount):
        self.experience += amount
        if self.experience >= 100:
            self.level_up()

    def level_up(self):
        self.level += 1
        self.health += 20  # Increased health gain on level up
        self.power += 10  # Increased power gain on level up
        self.special_power += 20  # Increased special power gain on level up
        self.experience = 0
        print(f"{self.name} leveled up! Now at Level {self.level}!")

class Enemy(Character):
    def __init__(self, name):
        health = random.randint(100, 200)  # Overpowered health
        power = random.randint(30, 60)  # Overpowered power
        special_power = random.randint(60, 100)  # Overpowered special power
        super().__init__(name, health, power, special_power)
        self.special_moves = self.initialize_special_moves()

    def initialize_special_moves(self):
        return [
            {'name': 'Dark Slash', 'damage': random.randint(50, 90)},
            {'name': 'Cursed Magic', 'damage': random.randint(70, 110)}
        ]

class Item:
    def __init__(self, name, heal_amount):
        self.name = name
        self.heal_amount = heal_amount

class HealthPotion(Item):
    def __init__(self):
        super().__init__("Health Potion", heal_amount=random.randint(30, 60))

class AttackBoost(Item):
    def __init__(self):
        super().__init__("Attack Boost", heal_amount=random.randint(15, 30))

class Game:
    def __init__(self):
        self.player = None
        self.enemy = None
        self.rounds = 0
        self.enemies_defeated = 0
        self.story_progression = 0

    def create_player(self):
        name = input("Enter your character's name: ")
        character_class = self.choose_character_class()
        self.player = Player(name, character_class)

    def choose_character_class(self):
        classes = ["Warrior", "Mage", "Rogue"]
        print("Choose your character class:")
        for i, character_class in enumerate(classes, 1):
            print(f"{i}. {character_class}")
        choice = int(input("Enter choice (1-3): "))
        return classes[choice - 1]

    def create_enemy(self):
        names = ["Evil Villain", "Shadow Warrior", "Dark Knight", "Ancient Beast"]
        name = random.choice(names)
        self.enemy = Enemy(name)

    def game_round(self):
        while self.player.is_alive() and self.enemy.is_alive():
            print("\nChoose your action:")
            print("1. Attack")
            print("2. Special Attack")
            print("3. Heal")
            choice = input("Enter choice (1/2/3): ")

            if choice == "1":
                self.player.attack(self.enemy)
            elif choice == "2":
                self.player.special_attack(self.enemy)
            elif choice == "3":
                self.player.heal()

                 # Allow player to use an item
            else:
                print("Invalid choice, try again.")
                continue

            if self.enemy.is_alive():
                self.enemy.attack(self.player)

            print(self.player.status())
            print(self.enemy.status())

        if self.player.is_alive():
            print(f"{self.player.name} wins this round!")
            self.player.gain_experience(50)
            self.rounds += 1
            self.enemies_defeated += 1
            print(f"Rounds won: {self.rounds}")
        else:
            print(f"{self.enemy.name} wins! Game Over.")

    def main(self):
        print("Welcome to the Overpowered Fighting Game!")
        self.create_player()

        story = [
            "You enter the arena, ready to face your first opponent.",
            "The crowd roars as you prepare for battle.",
            "With each victory, you grow stronger, and new challenges await."
        ]

        for part in story:
            print(part)

        while self.player.is_alive():
            self.create_enemy()
            print(f"\nA wild enemy appears: {self.enemy.name}!")
            print(self.enemy.status())

            self.game_round()
            if not self.player.is_alive():
                break

        if self.enemies_defeated > 0:
            print(f"Congratulations {self.player.name}, you defeated {self.enemies_defeated} enemies!")

if __name__ == "__main__":
    Game().main()

